// Generate comprehensive intelligence report
const items = $input.all();
const citationData = items[0].json;
const patterns = items[1].json;
const gaps = items[2].json;
const competitors = items[3].json;

// Calculate key metrics
const totalCitations = citationData.total_citations || 0;
const weekOverWeekGrowth = citationData.week_over_week_growth || 0;
const topDomains = citationData.top_domains || [];
const engineDistribution = citationData.engine_distribution || {};

// Extract insights
const highOpportunityGaps = gaps.gaps ? gaps.gaps.filter(gap => gap.opportunity_score >= 0.8) : [];
const patternInsights = patterns.insights || {};
const competitorThreats = competitors.threats || [];
const competitorOpportunities = competitors.opportunities || [];

// Generate summary
const summary = {
  period: `${new Date(Date.now() - 7 * 24 * 60 * 60 * 1000).toLocaleDateString()} - ${new Date().toLocaleDateString()}`,
  total_citations: totalCitations,
  week_over_week_growth: `${weekOverWeekGrowth > 0 ? '+' : ''}${(weekOverWeekGrowth * 100).toFixed(1)}%`,
  key_metrics: {
    citations_per_day: Math.round(totalCitations / 7),
    unique_domains_cited: topDomains.length,
    engines_active: Object.keys(engineDistribution).length
  },
  top_performing_domains: topDomains.slice(0, 5),
  engine_distribution: engineDistribution,
  high_opportunity_gaps: highOpportunityGaps.slice(0, 10),
  pattern_insights: patternInsights.recommendations || [],
  competitor_analysis: {
    threats: competitorThreats.slice(0, 5),
    opportunities: competitorOpportunities.slice(0, 5)
  },
  recommendations: [
    ...patternInsights.recommendations || [],
    ...highOpportunityGaps.slice(0, 3).map(gap => ({
      type: 'content_creation',
      description: `Create ${gap.suggested_content_type} for "${gap.query_text}"`,
      priority: gap.priority,
      opportunity_score: gap.opportunity_score
    }))
  ].slice(0, 10)
};

return { json: summary };

// Format report for Slack
const data = $json;

const formatPercentage = (value) => {
  return value >= 0 ? `üìà ${value}` : `üìâ ${value}`;
};

const formatList = (items, limit = 5) => {
  return items.slice(0, limit).map((item, index) => 
    `${index + 1}. ${typeof item === 'string' ? item : item.name || item.description || JSON.stringify(item)}`
  ).join('\n');
};

const slackMessage = {
  text: "üìä Weekly AI Search Intelligence Report",
  attachments: [
    {
      color: "#36a64f",
      title: `Performance Summary (${data.period})`,
      fields: [
        {
          title: "Total Citations",
          value: data.total_citations.toString(),
          short: true
        },
        {
          title: "Week-over-Week Growth",
          value: formatPercentage(data.week_over_week_growth),
          short: true
        },
        {
          title: "Daily Average",
          value: data.key_metrics.citations_per_day.toString(),
          short: true
        },
        {
          title: "Unique Domains",
          value: data.key_metrics.unique_domains_cited.toString(),
          short: true
        }
      ],
      ts: Math.floor(Date.now() / 1000)
    },
    {
      color: "#ff9900",
      title: "üéØ Top Content Opportunities",
      text: data.high_opportunity_gaps.slice(0, 5).map((gap, index) => 
        `${index + 1}. **${gap.query_text}** (Score: ${gap.opportunity_score.toFixed(2)})\n   ‚Üí ${gap.suggested_content_type}`
      ).join('\n\n'),
      mrkdwn_in: ["text"]
    },
    {
      color: "#ff6b6b",
      title: "‚ö†Ô∏è Competitor Threats",
      text: data.competitor_analysis.threats.length > 0 ? 
        formatList(data.competitor_analysis.threats) : 
        "No significant threats detected",
      mrkdwn_in: ["text"]
    },
    {
      color: "#4ecdc4",
      title: "üí° Key Recommendations",
      text: data.recommendations.slice(0, 5).map((rec, index) => 
        `${index + 1}. ${rec.description} (${rec.priority} priority)`
      ).join('\n'),
      mrkdwn_in: ["text"]
    }
  ]
};

return { json: slackMessage };

